import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './App.css';
import Footer from './Footer';
import Loader from './Loader';
import Main from './Main';
import Navigation from './Navigation';
import { useEffect } from 'react';
import {faCircleArrowUp } from '@fortawesome/free-solid-svg-icons';

function App() {
  myFunction();
  useScript();

  const script = document.createElement('script');
    script.src = 'https://cdnjs.cloudflare.com/ajax/libs/flickity/1.0.0/flickity.pkgd.js';
    script.async = true;
    document.body.appendChild(script);
  return (
    <div className="App darkmode" >
      <Loader/>
      <Navigation/>
      <a href="home" className="back-to-top-btn btn btn-primary" title="Back to top"><FontAwesomeIcon icon={ faCircleArrowUp} /></a>
      <Main/>
      <Footer/>
    </div>
  );
}

function myFunction() {
  setTimeout(showPage, 1000);
}

function showPage() {
  document.querySelector(".loading-screen").style.display = "none";
}

const useScript = url => {
  useEffect(() => {
    const urls = [
      'https://cdnjs.cloudflare.com/ajax/libs/flickity/1.0.0/flickity.pkgd.js',
      "https://cdnjs.cloudflare.com/ajax/libs/jquery/3.3.1/jquery.min.js",
    ]

    urls.forEach(url => {
      const script = document.createElement('script');
      script.src = url;
      script.async = true;
      document.body.appendChild(script);
    });
  }, []);
};
(function ($) {

  /*--Scroll Back to Top Button Show--*/

  $(window).scroll(function(){
      if ($(this).scrollTop() > 100) {
          $('#back-to-top').fadeIn();
      } else {
          $('#back-to-top').fadeOut();
      }
  });

  //Click event scroll to top button jquery

  $('#back-to-top').click(function(){

      $('html, body').animate({scrollTop : 0},600);
      return false;
  });

  })(jQuery);


export default App;
