import React, { useEffect } from 'react'
import { FaHtml5,FaReact,FaPython } from 'react-icons/fa';
import { IoLogoCss3 } from 'react-icons/io';
import { TbBrandBootstrap } from 'react-icons/tb';
import { DiJavascript, DiIllustrator } from 'react-icons/di';
import { SiJquery, SiSass, SiPhpstorm, SiMui } from 'react-icons/si';
import { BsVectorPen } from 'react-icons/bs';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.bundle';


const Icons = (props) => {
  const icons = [
    {name: 'html',icon:<FaHtml5/>,color:"#d5441e"},
    {name: 'css',icon:<IoLogoCss3/>,color:"#2e7dbb"},
    {name: 'bootstrap',icon:<TbBrandBootstrap/>,color:"#973cc1"},
    {name: 'javascript',icon:<DiJavascript/>,color:"#c1c03c"},
    {name: 'react',icon:<FaReact/>,color:"#4e3cc1"},
    {name: 'jquery',icon:<SiJquery/>,color:"#7f2ebb"},
    {name: 'sass',icon:<SiSass/>,color:"#bb2e9d"},
    {name: 'php',icon:<SiPhpstorm/>,color:"#5C55A1"},
    {name: 'python',icon:<FaPython/>,color:"#Ffde57"},
    {name: 'mui',icon:<SiMui/>,color:"#1234EF"},
    {name: 'vector',icon:<BsVectorPen/>,color:"#557A46"},
    {name: 'illustrator',icon:<DiIllustrator/>,color:"#F8a829"},
]
useEffect(() => {
  const inViewport = (entries, observer) => {
    entries.forEach(entry => {
      entry.target.classList.toggle("is-inViewport", entry.isIntersecting);
    });
  };
  
  const Obs = new IntersectionObserver(inViewport);
  const obsOptions = {}; //See: https://developer.mozilla.org/en-US/docs/Web/API/Intersection_Observer_API#Intersection_observer_options
  
  // Attach observer to every [data-inviewport] element:
  const ELs_inViewport = document.querySelectorAll('[data-inviewport]');
  ELs_inViewport.forEach(EL => {
    Obs.observe(EL, obsOptions);
  });
  
}, []);

    let icon = hasValueInArrayOfDictionaries(props.icon, icons);
    

  return (
    <div data-toggle="tooltip"  data-placement="top" title={props.name}>
      <div   data-inviewport="show-fade" style={{"--color": icon.color,"--delay":props.delay}} class="file-icons tooltip bs-tooltip-top" role="tooltip">{icon.icon}</div>
    </div>
  )
}

function hasValueInArrayOfDictionaries(x, arrayOfDictionaries) {
  for (const dictionary of arrayOfDictionaries) {
    if (Object.values(dictionary).includes(x)) {
      return dictionary;
    }
  }
  return false;
}
export default Icons
