import React, { useEffect } from 'react'
import { FaHtml5,FaReact } from 'react-icons/fa';
import { IoLogoCss3 } from 'react-icons/io';
import { TbBrandBootstrap } from 'react-icons/tb';
import { DiJavascript } from 'react-icons/di';
import { SiJquery, SiSass } from 'react-icons/si';


const Icons = (props) => {
  const icons = [
    {name: 'html',icon:<FaHtml5/>},
    {name: 'css',icon:<IoLogoCss3/>},
    {name: 'bootstrap',icon:<TbBrandBootstrap/>},
    {name: 'javascript',icon:<DiJavascript/>},
    {name: 'react',icon:<FaReact/>},
    {name: 'jquery',icon:<SiJquery/>},
    {name: 'sass',icon:<SiSass/>},
]
useEffect(() => {
  // Call the function on component mount and page refresh
  const tallestHeight = setTargetElementHeight();

  // Save the tallestHeight to LocalStorage on each render
  localStorage.setItem('tallestHeight', tallestHeight);
}, []);

    let icon = hasValueInArrayOfDictionaries(props.icon, icons);
    

  return (
    <div>
      <div data-inviewport="show-fade" style={{"--color": props.color,"--delay":props.delay}} class="file-icons">{icon.icon}</div>
    </div>
  )
}

function hasValueInArrayOfDictionaries(x, arrayOfDictionaries) {
  for (const dictionary of arrayOfDictionaries) {
    if (Object.values(dictionary).includes(x)) {
      return dictionary;
    }
  }
  return false;
}
export default Icons
