import React, { forwardRef, useEffect } from 'react'
import Icons from '../MainComponent/Icons';

const ProjectList = forwardRef((props,ref) => {
    //var image_url = '';
    try {
        // Code that might throw an error
        //image_url = require(`../images/project_images/${props.image}`);
      } catch (error) {
        // Code to handle the error
        console.error('An error occurred:', error);
      }
	useEffect(() => {
	const elementToggleFunc = function (elem) { elem.classList.toggle("active"); }
    
	const testimonialsItem = document.querySelectorAll("[data-testimonials-item]");
    
    const modalContainer = document.querySelector("[data-modal-container]");
    const modalCloseBtn = document.querySelector("[data-modal-close-btn]");
    const overlay = document.querySelector("[data-overlay]");

    const modalText = document.querySelector("[data-modal-text]");
    const modalImg = document.querySelector("[data-modal-img]");
    const modalCategory = document.querySelector("[data-modal-category]");
    const modalTitle = document.querySelector("[data-modal-title]");
    const modalTech = document.querySelector("[data-modal-technologies]");


    
    const addTestimonialsModalFunc = function () {
        modalContainer.classList.add("active");
        overlay.classList.add("active");
        }
    const removeTestimonialsModalFunc = function () {
            modalContainer.classList.remove("active");
            overlay.classList.remove("active");
        }
     
    for (let i = 0; i < testimonialsItem.length; i++) {
        
        testimonialsItem[i].addEventListener("click", function () {
            console.log("Item: "+i);
            
            modalImg.src = this.querySelector("[data-image]").src;
            modalTitle.textContent = this.querySelector("[data-title]").textContent;
            modalTitle.style.color = this.querySelector("[data-theme]").textContent;
            modalText.textContent = this.querySelector("[data-description]").textContent;
            modalCategory.textContent = this.querySelector("[data-category]").textContent;
            const datastrc = this.querySelectorAll("[data-technologies]");
           
            addTestimonialsModalFunc();
        });
       
        }
        
       

        // add click event to modal close button
        modalCloseBtn.addEventListener("click", removeTestimonialsModalFunc);
        overlay.addEventListener("click", removeTestimonialsModalFunc);

        

    // custom select variables
    const select = document.querySelector("[data-select]");
    const selectItems = document.querySelectorAll("[data-select-item]");
    const selectValue = document.querySelector("[data-selecct-value]");
    const filterBtn = document.querySelectorAll("[data-filter-btn]");

    select.addEventListener("click", function () { elementToggleFunc(this); });

    // add event in all select items
    for (let i = 0; i < selectItems.length; i++) {
    selectItems[i].addEventListener("click", function () {

        let selectedValue = this.innerText.toLowerCase();
        selectValue.innerText = this.innerText;
        elementToggleFunc(select);
        filterFunc(selectedValue);

    });
    }

    // filter variables
    const filterItems = document.querySelectorAll("[data-filter-item]");
    const filterFunc = function (selectedValue) {

        for (let i = 0; i < filterItems.length; i++) {

            if (selectedValue === "all") {
            filterItems[i].classList.add("active");
            } else if (selectedValue === filterItems[i].dataset.category) {
                
            filterItems[i].classList.add("active");
            } else {
            filterItems[i].classList.remove("active");
            }
            

        }

    }


    for (let i = 0; i < filterBtn.length; i++) {
        
    filterBtn[i].addEventListener("click", function () {
        const selectedValue = this.innerText.toLowerCase();
        selectValue.innerText = this.innerText;
        filterFunc(selectedValue);
        

    });
}

	  }, []);

	console.log(props.type.toLowerCase());
    return (
        <div>
        <li ref={ref} class="project-item active" data-filter-item="" data-category={props.type.toLowerCase()}>
            <div data-testimonials-item>
                <figure class="project-img">
                    <div class="project-item-icon-box">
                        <ion-icon name="eye-outline" role="img" class="md hydrated" aria-label="eye outline"></ion-icon>
                    </div>
                        <img src={props.image} data-image alt="brawlhalla" loading="lazy"/>         
                </figure>
                <h3 class="project-title" data-title>{props.title}</h3>
                <p class="project-category" data-category>{props.type}</p>
                <p class="project-description d-none" data-description>{props.description}</p>
                <p class="project-theme d-none" data-theme>{props.theme}</p>
                {props.technologies.map((item,index) => (
                        <p class="project-technologies d-none" data-technologies={index}>{item.name},{item.icon}</p>
                    ))}
            </div>
        </li>
        <div class="modal-container" data-modal-container>

            <div class="overlay" data-overlay></div>

            <section class="testimonials-modal p-5">

                <button class="modal-close-btn" data-modal-close-btn>
                <ion-icon name="close-outline"></ion-icon>
                </button>

                <div  class="project-grid  row">
            <div  class="project">
                <div  class="flex-container  p-4 border bg-light">
                    <h4 data-modal-title className='mb-3' style={{"color": "red"}}>Hello World</h4>
                    <div className='mb-3'><small  data-modal-category>Landing Page</small></div>
                    <p class="d-inline-block mb-3" data-modal-text>
                    
                    </p>
                    {props.technologies.map((item,index) => (
                        
                        <Icons key={ref} index={index}  delay={((index+1)*250)+"ms"} color={item.color} name={item.name} icon={item.icon}/>
                    ))}
                </div>
            </div>
            <a href="# " target="_blank" rel="noreferrer" >
                <img loading="lazy" class="rounded float-start portfolio-image" src="" style={{width:"-webkit-fill-available"}} data-modal-img alt="" />
            </a>
        </div>
            </section>

        </div>
        </div>
    )
})




export default ProjectList
