                <button data-select-item="">Application</button>
            </li>

            <li class="select-item">
                <button data-select-item="">Landing Page</button>
            </li>

            <li class="select-item">
                <button data-select-item="">Graphic Design</button>
            </li>
            </ul>
        </div>
        
        <ul class="project-list">
        {mydata.map((item) => (
                <ProjectList key={item.id} type={item.type} image={item.image_url} theme={item.theme} technologies={item.technologies} title={item.name} description={item.description} />
