import React, { useContext } from 'react'
import image from "../images/asdsd.jpg";
import Icons from './Icons';
import { MediaContext } from '../MediaContext';

const Project = (props) => {  
    const copy = new CopyWebpackPlugin([
        { from: ROOT + '/images', to: 'images' }
    ]);
  return (
    <div key={props.key}> 
        <div data-inviewport="fade-up" class="project-grid  row mb-5">
            <div class="project">
                <div class="flex-container  p-4 border bg-light">
                    
                    <h4 className='mb-3' style={{"--theme-color": props.theme}}>{props.title}</h4>
                
                    
                    <p class="d-inline-block mb-3">
                    {props.description}
                    </p>
                    <div class="project__icons ">
                    {props.technologies.map(item => (
                        <Icons color={item.color} name={item.name} icon={item.icon}/>
                    ))}
                    </div>
                </div>
            </div>
            <div data-inviewport="fade-right" class="">
                <img class="rounded float-start portfolio-image" style={{width:"-webkit-fill-available"}} src={image} alt="" />
            </div>
        </div>
    </div>
  )
}

export default Project
