import React from 'react'
import Icons from './Icons';


const Project = ({props},key) => {  
    var image_url = '';
    try {
        // Code that might throw an error
        image_url = require(`../images/project_images/${props.image}`);
      } catch (error) {
        // Code to handle the error
        console.error('An error occurred:', error);
      }
    
  return (
    <div key={props.key}> 
        <div  class="project-grid  row mb-5">
            <div class="project">
                <div class="flex-container  p-4 border bg-light">
                    <h4 className='mb-3' style={{"--theme-color": props.theme}}>{props.title}</h4>
                    <p class="d-inline-block mb-3">
                    {props.description}
                    </p>
                    <div class="project__icons ">
                    {props.technologies.map((item,index) => (
                        
                        <Icons key={index} index={index}  delay={((index+1)*250)+"ms"} color={item.color} name={item.name} icon={item.icon}/>
                    ))}
                    </div>
                </div>
            </div>
            <div  class="">
                <img key={props.key} loading="lazy" class="rounded float-start portfolio-image" src={image_url} style={{width:"-webkit-fill-available"}}  alt={props.image} />
            </div>
        </div>
    </div>
  )
}

export default Project
