import React, { useEffect } from 'react'
import './Main.css';
import './Animation.css';
import background from "./images/profile_image.jpg";

import { FaHtml5,FaReact } from 'react-icons/fa';
import { BsChevronDown } from 'react-icons/bs';
import { BiMessageDots } from 'react-icons/bi';
import { IoLogoCss3 } from 'react-icons/io';
import { TbBrandBootstrap } from 'react-icons/tb';
import { SiJavascript } from 'react-icons/si';

import Project from './MainComponent/Project';
import mydata from './myData/data.json'


function Main() {
    useEffect(() => {
        

        var TxtType = function(el, toRotate, period) {
            this.toRotate = toRotate;
            this.el = el;
            this.loopNum = 0;
            this.period = parseInt(period, 10) || 2000;
            this.txt = '';
            this.tick();
            this.isDeleting = false;
        };
    
        TxtType.prototype.tick = function() {
            var i = this.loopNum % this.toRotate.length;
            var fullTxt = this.toRotate[i];
    
            if (this.isDeleting) {
            this.txt = fullTxt.substring(0, this.txt.length - 1);
            } else {
            this.txt = fullTxt.substring(0, this.txt.length + 1);
            }
    
            this.el.innerHTML = '<span class="wrap">'+this.txt+'</span>';
    
            var that = this;
            var delta = 200 - Math.random() * 100;
    
            if (this.isDeleting) { delta /= 2; }
    
            if (!this.isDeleting && this.txt === fullTxt) {
            delta = this.period;
            this.isDeleting = true;
            } else if (this.isDeleting && this.txt === '') {
            this.isDeleting = false;
            this.loopNum++;
            delta = 500;
            }
    
            setTimeout(function() {
            that.tick();
            }, delta);
        };
    
        window.onload = function() {
            var elements = document.getElementsByClassName('typewrite');
            for (var i=0; i<elements.length; i++) {
                var toRotate = elements[i].getAttribute('data-type');
                var period = elements[i].getAttribute('data-period');
                if (toRotate) {
                  new TxtType(elements[i], JSON.parse(toRotate), period);
                }
            }
            // INJECT CSS
            var css = document.createElement("style");
            css.type = "text/css";
            css.innerHTML = ".typewrite > .wrap { border-right: 0.08em solid #fff}";
            document.body.appendChild(css);
        };

        window.addEventListener('resize', setTargetElementHeight);

        const inViewport = (entries, observer) => {
            entries.forEach(entry => {
              entry.target.classList.toggle("is-inViewport", entry.isIntersecting);
            });
          };
          
          const Obs = new IntersectionObserver(inViewport);
          const obsOptions = {}; //See: https://developer.mozilla.org/en-US/docs/Web/API/Intersection_Observer_API#Intersection_observer_options
          
          // Attach observer to every [data-inviewport] element:
          const ELs_inViewport = document.querySelectorAll('[data-inviewport]');
          ELs_inViewport.forEach(EL => {
            Obs.observe(EL, obsOptions);
          });
          
        },[])

  return (
    <div id="home">
      <main className="container animate-bottom" id="main">
        <div className="section d-flex flex-wrap justify-content-xs-evenly  justify-content-sm-evenly justify-content-lg-center justify-content-xl-between">
            <div className="content col order-sm-last order-lg-first order-md-last">
                <p data-inviewport="fade-right" className="typewrite" data-period="2000" data-type='["I am Creative.", "I love to Design.", "I love to Develop.", "I love to Program.", "I love to Invent." ]'> </p>
                <h1 data-inviewport="fade-up" className="text-md-center darkmode text-xs-center text-xl-start">Gianfranco <span data-inviewport="fade-right">Fernandez</span></h1>
                <p data-inviewport="fade-right" className="description text-xs-center ">Hi there! I'm a recent Information Tech graduate from Cebu Institute of Technology University. In my free time, I enjoy staying up-to-date with tech trends and learning new programming languages. I am eager to begin my career in the IT industry and make a positive impact with my skills and knowledge.</p>
                <a href="#portfolio" type="button" className="btn btn-danger position-xs-center">View Projects</a>
            </div>
            <div className="image order-sm-first order-lg-last order-md-first position-xs-center" >
                <div className="large-circle " style={{backgroundImage: `url(${background})`}} data-inviewport="fade-down">
                    <div className="small-circle planet1"></div>
                    <div className="small-circle planet2"></div>
                    <div className="small-circle planet3"></div>
                    <div className="small-circle planet4"></div>
                    <div className="small-circle planet5"></div>
                </div>
            </div>
        </div>
        <div class="section  d-flex flex-wrap justify-content-sm-between justify-content-lg-between">
            <div class="social-icons position-xs-center  d-flex flex-wrap justify-content-lg-between" >
                <a href="# "><i class="fa-brands fa-facebook"></i></a>
                <a href="# "><i class="fab fa-twitter"></i></a>
                <a href="# "><i class="fab fa-linkedin-in"></i></a>
            </div>
            <div class="scroll-down  d-xs-none d-sm-none d-md-block d-md-none d-lg-block" >
                <a href="#about" class="d-flex flex-column">
                <i><BsChevronDown/></i>
                <i><BsChevronDown/></i>
                <i><BsChevronDown/></i>
                </a>
            </div>
            <div class="position-xs-center">
                <button type="button" class="btn btn-danger  fw-bolder">Download CV</button>
            </div>
        </div>
        <div id="about" class="section my-5 ">
            <h2 class="">About</h2>
            <p>Here's more information about me.</p>
            <div class="d-flex  flex-wrap gap-5">
                <div class="segment mt-5 flex-fill order-sm-last order-lg-first order-md-last">
                    <h6>Educational Background</h6>
                    <div class="d-grid gap-3">
                        <div class="card col-sm-12 col-md-12 col-lg-12 col-xl-10 animate-item" data-inviewport="fade-right" >
                            <div class="card-body">
                                <div class="d-flex justify-content-between">
                                    <h5 class="card-title">College Graduate</h5>
                                    <span class="card-icon"><i class="fab fa-linkedin-in"></i></span>
                                </div>
                                <h6 class="card-subtitle mb-2 text-muted">Bachelors of Science in Information Technology</h6>
                                <p class="card-text">Cebu Institute of Technology University, Cebu City - <b>June 2023</b></p>
                            </div>
                        </div>
                        <div class="card col-sm-12 col-md-12 col-lg-12 col-xl-10 animate-item" data-inviewport="fade-up">
                            <div class="card-body">
                                <div class="d-flex justify-content-between">
                                    <h5 class="card-title">OJT Experience</h5>
                                    <span class="card-icon"><i class="fab fa-linkedin-in"></i></span>
                                </div>
                                <h6 class="card-subtitle mb-2 text-muted">Wordpress Developer/Web Designer</h6>
                                <p class="card-text">ELink System and Concepts Corp, Cebu City - <b>December 2022</b></p>
                            </div>
                        </div>
                    </div>    
                    <div class="mt-5">
                        <h6>Main Skills</h6>
                        <div class="d-grid gap-3">
                            <div class="card col-sm-12 col-md-12 col-lg-12 col-xl-10" data-inviewport="fade-right">
                                <div class="card-body">
                                    <div class="d-flex justify-content-between">
                                        <h5 class="card-title">Frontend Developer</h5>
                                        <span class="card-icon"><i class="fab fa-linkedin-in"></i></span>
                                    </div>
                                    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                </div>
                            </div>
                            <div class="card col-sm-12 col-md-12 col-lg-12 col-xl-10" data-inviewport="fade-up">
                                <div class="card-body">
                                    <div class="d-flex justify-content-between">
                                        <h5 class="card-title">UI/UX Designer</h5>
                                        <span class="card-icon"><i class="fab fa-linkedin-in"></i></span>
                                    </div>
                                    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div class="technologies__section flex-fill mt-5">
                    <h6>Technologies</h6>
                    <div class="">
                        <div class="col">
                            <div class="social-icons text-center">
                                <div style={{"--color":"#d5441e"}}  data-inviewport="fade-up" class="item">
                                    <div class="file-icons "><i><FaHtml5/></i></div>
                                <p>HTML</p></div>
                                <div style={{"--color":"#2e7dbb"}} data-inviewport="fade-left" class="item">
                                    <div class="file-icons"><i><IoLogoCss3/></i></div>
                                <p>CSS</p></div>
                                <div style={{"--color":"#c1c03c"}} data-inviewport="fade-down" class="item">
                                    <div class="file-icons"><i><SiJavascript/></i></div>
                                <p>JS</p></div>
                                <div style={{"--color":"#973cc1"}} data-inviewport="fade-right" class="item">
                                    <div class="file-icons"><i><SiJavascript/></i></div>
                                <p>REACT</p></div>
                                <div style={{"--color":"#4e3cc1"}}  data-inviewport="fade-up" class="item"><div class="file-icons"></div>
                                <p>Bootstrap</p></div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        <div id="project" class="section  my-5 mb-5 pt-5">
            <h2 class="">Projects</h2>
            <p>Here are the few projects I worked.</p>
            <div class="">
            {mydata.map(item => (
                <Project key={item.id} technologies={item.technologies} title={item.name} description={item.description} />
                ))}
            </div>
            <a href="# " type="button" class="btn btn-danger my-5 position-relative start-50 translate-middle main__button">View More</a>
        </div>
        <section class="my-5" id="services">
            <h3>Collaborate with brands and agencies to create impactful results.</h3>
            <div class="d-flex my-5 flex-wrap flex-row justify-content-center">
                <div class="product" id="tenor">
                    <i><FaHtml5/></i>
                    <p class="title">Website Design</p>
                    <p class="description">Transforming ideas into exceptional web and mobile app experiences.</p>
                </div>
                <div class="product" id="tenor">
                    <i><FaHtml5/></i>
                    <p class="title">Design & Creative</p>
                    <p class="description">Crafting visually stunning designs that connect with your audience.</p>
                </div>
            </div>
        </section>
        <section id="contact" class="contact-section">
            <div class="contact-section-header">
              <h2>Tell me about your next project...</h2>
              <p class="mt-5">What's your preferred way of enjoying coffee?</p>
            </div>
            <div class="contact-links">
              <a
                href="https://dribbble.com/"
                target="_blank"  rel="noopener noreferrer"
                class="btn contact-details" 
                ><i class="fab fa-facebook-square"></i> Dribbble</a>
              <a
                id="profile-link"
                href="https://github.com/"
                target="_blank"  rel="noopener noreferrer"
                class="btn contact-details"
                ><i class="fab fa-github"></i> GitHub</a>
              <a
                href="https://Linkedin.com/"
                target="_blank"  rel="noopener noreferrer"
                class="btn contact-details"
                ><i class="fab fa-twitter"></i> Linkedin</a>
              <div class="d-flex col-12 my-5 flex-wrap flex-row justify-content-evenly">
              <a href="mailto:kawsarvy.design@gmail.com" 
                type='button' target="_blank" 
                rel="noopener noreferrer" 
                class="submit">Get in touch  &nbsp; &nbsp;  <BiMessageDots /></a>
              </div>
            </div>
          </section>
    </main>
    </div>
  );
}

function setTargetElementHeight() {

    const flexContainers = document.querySelectorAll('.flex-container');
    const targetElement = document.querySelectorAll('.portfolio-image');
  

    let tallestHeight = 0;
    for (let i = 0; i < flexContainers.length; i++) {
      const flexContainer = flexContainers[i];
      
      const computedStyle = getComputedStyle(flexContainer);
      const heightValue = computedStyle.getPropertyValue('height');
      const height = parseFloat(heightValue.replace('px', ''));
      if (height > tallestHeight) {
        tallestHeight = height;
      }
      targetElement[i].style.height = `${tallestHeight}px`;
    }

  }
  setTargetElementHeight();
  

