import React, { useEffect, useState } from 'react'
import Footer from '../Footer';
import Loader from '../Loader';
import Navigation from '../Navigation';
import '../Main.css';
import './AllProjects.css';
//import mydata from '../myData/data.json';
import db from '../firebase';
import ProjectList from './ProjectList';
function AllProjects() {
    myFunction();
    const [projects,setProjects] = useState([]);
    try {
        projects.map(({id,item}) =>  (console.log("Title: "+item.color )));
    }catch (e) {

    }
    function setTargetElementHeight() {
      const flexContainers = document.querySelectorAll('.project .flex-container');
      const targetElement = document.querySelectorAll('.project-grid > a .portfolio-image');
      let tallestHeight = localStorage.getItem('tallestHeight') || 0;
    
      for (let i = 0; i < flexContainers.length; i++) {
        const computedStyle = getComputedStyle(flexContainers[i]);
        const heightValue = computedStyle.getPropertyValue('height');
        const height = parseFloat(heightValue.replace('px', ''));
    
        if (height > tallestHeight) {
          tallestHeight = height;
        }
    
        targetElement[i].style.height = `${tallestHeight}px`;
      }
    
      // Save the tallestHeight value to LocalStorage
      localStorage.setItem('tallestHeight', tallestHeight);
    
      return tallestHeight;
    }
  
    useEffect(() => {
      db.collection('project').onSnapshot(snapshot => {
        setProjects(snapshot.docs.map(doc =>({id:doc.id,item:doc.data()})));
      });
      const tallestHeight = setTargetElementHeight();
    
        // Save the tallestHeight to LocalStorage on each render
        localStorage.setItem('tallestHeight', tallestHeight);
    },[]);

  return (
    <div className="App darkmode d-flex justify-content-between flex-column" style={{height:'100vh'}}>
    <Loader/>
      <Navigation/>
        <article class="portfolio active container mb-5" data-page="portfolio">
          <header>
          <h2 class="h2 article-title">Portfolio</h2>
          </header>
        <section class="projects">
        <ul class="filter-list">

            <li class="filter-item">
            <button class="active" data-filter-btn="">All</button>
            </li>

            <li class="filter-item">
            <button data-filter-btn="" class="">Application</button>
            </li>

            <li class="filter-item">
            <button data-filter-btn="" class="">Landing Page</button>
            </li>

            <li class="filter-item">
            <button data-filter-btn="">Graphic Design</button>
            </li>

        </ul>

        <div class="filter-select-box">
            <button class="filter-select" data-select="">

            <div class="select-value" data-selecct-value="">All</div>

            <div class="select-icon">
                <ion-icon name="chevron-down" role="img" class="md hydrated" aria-label="chevron down"></ion-icon>
            </div>

            </button>

            <ul class="select-list">

            <li class="select-item">
                <button data-select-item="">All</button>
            </li>
            <li class="select-item">
                <button data-select-item="">Application</button>
            </li>

            <li class="select-item">
                <button data-select-item="">Landing Page</button>
            </li>

            <li class="select-item">
                <button data-select-item="">Graphic Design</button>
            </li>
            </ul>
        </div>
        
        <ul class="project-list">
        {projects.map(({id,item}) => (
                <ProjectList key={id} link={item.link} type={item.type} image={item.image_url} theme={item.theme} technologies={item.technologies} title={item.name} description={item.description} />
                ))}  
        </ul>

        <div class="modal-container" data-modal-container>

            <div class="overlay" data-overlay></div>

            <section class="testimonials-modal p-5">

                <button class="modal-close-btn" data-modal-close-btn>
                <ion-icon name="close-outline"></ion-icon>
                </button>

                <div  class="project-grid  row">
            <div  class="project">
                <div  class="flex-container  p-4 border bg-light">
                    <h4 data-modal-title className='mb-3' style={{"color": "red"}}>Hello World</h4>
                    <div className='mb-3'><small  data-modal-category>Landing Page</small></div>
                    <p class="d-inline-block mb-3" data-modal-text>
                    
                    </p>
                    
                </div>
                <div className="modal_icons project__icons" data-modal-technologies></div>
            </div>
            <a href="# " target="_blank" rel="noreferrer" >
                <img loading="lazy" class="rounded float-start portfolio-image" src="" style={{width:"-webkit-fill-available"}} data-modal-img alt="" />
            </a>
        </div>
            </section>

        </div>


        </section>
        </article >
      <Footer/>
    </div>
  )
}
function myFunction() {
    setTimeout(showPage, 1000);
  }
  
  function showPage() {
    document.querySelector(".loading-screen").style.display = "none";
  }
  
export default AllProjects
