import React from 'react'
import image from "../images/pexels-tima-miroshnichenko-6169659.jpg";
import Icons from './Icons';

const Project = (props) => {    
  return (
    <div key={props.key}> 
        <div data-inviewport="fade-up" class="project-grid  row mb-5">
            <div class="project">
                <div class="flex-container  p-4 border bg-light">
                    {props.technologies.map(item => (
                    <h4 style={{"--theme-color": item.theme}}>{props.title}</h4>
                    ))}
                    
                    <p class="d-inline-block">
                    {props.description}
                    </p>
                    <div class="project__icons">
                    {props.technologies.map(item => (
                        <Icons  color={item.color} name={item.name} icon={item.icon}/>
                    ))}
                    </div>
                </div>
            </div>
            <div data-inviewport="fade-right" class="">
                <img class="rounded float-start portfolio-image" style={{width:"-webkit-fill-available"}} src={`${image}`} alt="" />
            </div>
        </div>
    </div>
  )
}

export default Project
