import React, { useEffect, useState } from 'react'
import './Main.css';
import './Animation.css';
import background from "./images/profile_image.jpg";
import paint_roller from "./images/icons/paint-roller.png";
import computer_icon from "./images/icons/responsive (1).png";
import rocket from "./images/icons/rocket.png";

import { FaDribbble, FaGithub, FaHtml5,FaLinkedinIn,FaReact } from 'react-icons/fa';
import { BsChevronDown } from 'react-icons/bs';
import { BiMessageDots } from 'react-icons/bi';
import { IoLogoCss3 } from 'react-icons/io';
import { TbBrandBootstrap } from 'react-icons/tb';
import { DiJavascript } from 'react-icons/di';

import Project from './MainComponent/Project';
//import mydata from './myData/data.json';
import db from './firebase';

function Main() {
    const [projects,setProjects] = useState([]);
    try {
        projects.map(({id,item}) =>  (console.log("Title: "+item.color )));
    }catch (e) {

    }
    
    useEffect(() => {
  
        db.collection("project").onSnapshot(snapshot => {
            setProjects(snapshot.docs.map(doc =>({id:doc.id,item:doc.data()})));
        });

        window.onload = setTargetElementHeight();
        var TxtType = function(el, toRotate, period) {
            this.toRotate = toRotate;
            this.el = el;
            this.loopNum = 0;
            this.period = parseInt(period, 10) || 2000;
            this.txt = '';
            this.tick();
            this.isDeleting = false;
        };
    
        TxtType.prototype.tick = function() {
            var i = this.loopNum % this.toRotate.length;
            var fullTxt = this.toRotate[i];
    
            if (this.isDeleting) {
            this.txt = fullTxt.substring(0, this.txt.length - 1);
            } else {
            this.txt = fullTxt.substring(0, this.txt.length + 1);
            }
    
            this.el.innerHTML = '<span class="wrap"> &nbsp;'+this.txt+'</span>';
    
            var that = this;
            var delta = 200 - Math.random() * 100;
    
            if (this.isDeleting) { delta /= 2; }
    
            if (!this.isDeleting && this.txt === fullTxt) {
            delta = this.period;
            this.isDeleting = true;
            } else if (this.isDeleting && this.txt === '') {
            this.isDeleting = false;
            this.loopNum++;
            delta = 500;
            }
    
            setTimeout(function() {
            that.tick();
            }, delta);
        };
    
        window.onload = function() {
            var elements = document.getElementsByClassName('typewrite');
            for (var i=0; i<elements.length; i++) {
                var toRotate = elements[i].getAttribute('data-type');
                var period = elements[i].getAttribute('data-period');
                if (toRotate) {
                  new TxtType(elements[i], JSON.parse(toRotate), period);
                }
            }
            // INJECT CSS
            var css = document.createElement("style");
            css.type = "text/css";
            css.innerHTML = ".typewrite > .wrap { border-right: 0.08em solid #fff}";
            document.body.appendChild(css);
        };

        

        const inViewport = (entries, observer) => {
            entries.forEach(entry => {
              entry.target.classList.toggle("is-inViewport", entry.isIntersecting);
            });
          };
          
          const Obs = new IntersectionObserver(inViewport);
          const obsOptions = {}; //See: https://developer.mozilla.org/en-US/docs/Web/API/Intersection_Observer_API#Intersection_observer_options
          
          // Attach observer to every [data-inviewport] element:
          const ELs_inViewport = document.querySelectorAll('[data-inviewport]');
          ELs_inViewport.forEach(EL => {
            Obs.observe(EL, obsOptions);
          });

          
          const flexContainers = document.querySelectorAll('.project .flex-container');
          const targetElement = document.querySelectorAll('.project-grid > div .portfolio-image');
          let tallestHeight = localStorage.getItem('tallestHeight');
      
          for (let i = 0; i < flexContainers.length; i++) {
              const computedStyle = getComputedStyle(flexContainers[i]);
              const heightValue = computedStyle.getPropertyValue('height');
              const height = parseFloat(heightValue.replace('px', ''));
      
              if (height = tallestHeight) {
                  tallestHeight = height;
              }
      
              targetElement[i].style.height = `${tallestHeight}px`;
          }
      
          // Save the tallestHeight value to LocalStorage
          localStorage.setItem('tallestHeight', tallestHeight);
        
        },[])

  return (
    <div id="home">
      <main className="container animate-bottom" id="main">
        <div className="section d-flex flex-wrap justify-content-xs-evenly  justify-content-sm-evenly justify-content-lg-center justify-content-xl-between">
            <div className="content col order-sm-last order-lg-first order-md-last">
                <p data-inviewport="fade-right" className="typewrite" data-period="2000" data-type='["I am Creative.", "I love to Design.", "I love to Develop.", "I love to Program.", "I love to Invent." ]'> </p>
                <h1 data-inviewport="fade-up" className="text-md-center darkmode text-xs-center text-xl-start">Gianfranco <span data-inviewport="fade-right">Fernandez</span></h1>
                <p data-inviewport="fade-right" className="description text-xs-center ">Hi there! I'm a recent Information Tech graduate from Cebu Institute of Technology University. In my free time, I enjoy staying up-to-date with tech trends and learning new programming languages. I am eager to begin my career in the IT industry and make a positive impact with my skills and knowledge.</p>
                <a href="#project" type="button" className="btn btn-danger position-xs-center">View Projects</a>
            </div>
            <div className="image order-sm-first order-lg-last order-md-first position-xs-center" >
                <div className="large-circle " style={{backgroundImage: `url(${background})`}} data-inviewport="fade-down">
                    <div className='orbit'>
                    <div className="rocketship rocket">
                        <img src={rocket} width="30" height="25" alt="" />
                        <div className='tail'></div></div>
                    </div>
                    
                    <div className="small-circle planet1"><i></i></div>
                    <div className="small-circle planet2"></div>
                    <div className="small-circle planet3"></div>
                    <div className="small-circle planet4"></div>
                    <div className="small-circle planet5"></div>
                </div>
            </div>
        </div>
        <div class="section  d-flex flex-wrap justify-content-sm-between justify-content-lg-between">
            <div class="social-icons position-xs-center  d-flex flex-wrap justify-content-lg-between" >
                <a target="_blank"  rel="noopener noreferrer" href="https://www.linkedin.com/in/gianfranco-fernandez-7443a7248"><i><FaLinkedinIn/></i></a>
                <a target="_blank"  rel="noopener noreferrer" href="https://dribbble.com/ghifere"><i><FaDribbble/></i></a>
                <a target="_blank"  rel="noopener noreferrer" href="https://github.com/Creamsandwich-06"><i ><FaGithub/></i></a>
            </div>
            <div class="scroll-down  d-xs-none d-sm-none d-md-block d-md-none d-lg-block" >
                <a href="#about" class="d-flex flex-column">
                <i><BsChevronDown/></i>
                <i><BsChevronDown/></i>
                <i><BsChevronDown/></i>
                </a>
            </div>
            <div class="position-xs-center">
                <button type="button" class="btn btn-danger  fw-bolder">Download CV</button>
            </div>
        </div>
        <div id="about" class="section my-5 ">
            <h2 class="">About</h2>
            <p>Here's more information about me.</p>
            <div class="d-flex  flex-wrap gap-5">
                <div class="segment col-lg-8   mt-5 flex-fill order-sm-last order-lg-first order-md-last">
                    <h6>Educational Background</h6>
                    <div class="d-grid gap-3">
                        <div class="card  animate-item" data-inviewport="fade-right" >
                            <div class="card-body">
